
public class LoopsPractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// By Myself
		for (int i= 2; i<= 10; i++)
		{
			System.out.println(i);
		}
		
		for (int i = 10; i >= 1; i--) {
			System.out.println(i);
		}
		 for (int i = 1; i <= 15; i= i+3) {
			 System.out.println(i);
		 }
		 
		 
		 for (int i =1; i <= 3; i++) {
			 System.out.println("Payal");
		 }
		 
		 for (int i = 3; i <=15; i=i+3) {
			 System.out.println(i);
		 }
		 
		 for (int i = 5; i>=1; i=i-2) {
			 System.out.println(i);
		 }
		 
		 
		 for (int i=1; i<=5; i++) {
			 System.out.println(i);
			 if(i== 3) {
				 break;
			 } 
			 
			 for (int j = 1;j<=5; j++) {
				 if(j==3) {
					 continue;}
				 System.out.println(j);
				 }
			 }
			 
		
		
		
		
		
		
		
		
		
		

	}

}
