
public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//checking boolean
		System.out.println(15 < 8);
		System.out.println(5 > 3);
		System.out.println(5 <= 3);
		System.out.println(5 >= 3);
		System.out.println(9 <= 9);
		System.out.println(9 >= 9);
		System.out.println(9 != 9);
		System.out.println(6 != 9);
		System.out.println(9 == 5);
		System.out.println(9 == 9);
		
		// logical operator 
		
		// checking logical operator for "&&"
		
		System.out.println(5 == 5 && 5 == 5);
		System.out.println(5 != 7 && 5 == 5);
		System.out.println(5 == 5 && 5 != 5);
		System.out.println(5 != 5 && 5 != 5);
		
		
		//checking logical operator for "OR"
		
		 System.out.println(6 == 10 || 6 == 10);
		 System.out.println(6 == 6 || 6 == 9);
		 System.out.println(6 == 2 || 6 == 6);
		 System.out.println(6 == 6 || 6 == 6);
		 
		 
	
		//checking logical operator for "NOT" 
		 
		 System.out.println(!(5 == 5));
		 System.out.println(!(12 == 8));
		
		
	}

}
