
public class testday3c {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Multiplication (2,3);
		 Multiplication (2,3,4);
		  Multiplication (2,3,4,5);
		  

		 }
	// two parameter
		 public static void Multiplication(int p, int q) {
		  System.out.println(p*q);
		 }
		 // 3 parameter
		 public static void Multiplication(int p, int q , int r) {
		  System.out.println(p*q*r);
		 }
		 //4 parameter
		 public static void Multiplication(int p, int q , int r, int s) {
		  System.out.println(p*q*r*s);
	}

}
